out() { printf "$1 $2\n" "${@:3}"; }
error() { out "==> ERROR:" "$@"; } >&2
msg() { out "==>" "$@"; }
msg2() { out "  ->" "$@";}
die() { error "$@"; exit 1; }
dexec() {
    cmd="$@"
    msg "$cmd"
    echo "[$(date +"%D %H:%M:%S")]:Exec:$cmd" >> $log
    eval $cmd >> $log 2>&1 || die "Die:$cmd"
}

